// –ü–æ–ª—É—á–∞–µ–º readings
const reading = $input.first().json;

// –ü–æ–ª—É—á–∞–µ–º —Å–æ—Å—Ç–æ—è–Ω–∏—è –∏–∑ workflow static data (—Ö—Ä–∞–Ω–∏—Ç—Å—è –º–µ–∂–¥—É –∑–∞–ø—É—Å–∫–∞–º–∏)
const staticData = $getWorkflowStaticData('global');
if (!staticData.alertStates) {
  staticData.alertStates = {};
}

const states = staticData.alertStates;

// –ü–æ—Ä–æ–≥–æ–≤—ã–µ –∑–Ω–∞—á–µ–Ω–∏—è
const thresholds = {
  high_temperature: { 
    field: 'outdoor_temperature', 
    max: 22, 
    message: 'üî• –ö–†–ò–¢–ò–ß–ù–û! –¢–µ–º–ø–µ—Ä–∞—Ç—É—Ä–∞ —Å–ª–∏—à–∫–æ–º –≤—ã—Å–æ–∫–∞—è' 
  },
  low_temperature: { 
    field: 'outdoor_temperature', 
    min: 18, 
    message: '‚ùÑÔ∏è –ö–†–ò–¢–ò–ß–ù–û! –¢–µ–º–ø–µ—Ä–∞—Ç—É—Ä–∞ —Å–ª–∏—à–∫–æ–º –Ω–∏–∑–∫–∞—è' 
  },
  high_humidity: { 
    field: 'outdoor_humidity', 
    max: 85, 
    message: 'üíß –ö–†–ò–¢–ò–ß–ù–û! –í–ª–∞–∂–Ω–æ—Å—Ç—å —Å–ª–∏—à–∫–æ–º –≤—ã—Å–æ–∫–∞—è' 
  },
  low_humidity: { 
    field: 'outdoor_humidity', 
    min: 20, 
    message: 'üèúÔ∏è –ö–†–ò–¢–ò–ß–ù–û! –í–ª–∞–∂–Ω–æ—Å—Ç—å —Å–ª–∏—à–∫–æ–º –Ω–∏–∑–∫–∞—è' 
  },
  low_water_level: { 
    field: 'water_level', 
    min: 5, 
    message: 'üö® –ö–†–ò–¢–ò–ß–ù–û! –£—Ä–æ–≤–µ–Ω—å –≤–æ–¥—ã –æ—á–µ–Ω—å –Ω–∏–∑–∫–∏–π!' 
  }
};

const alerts = [];
const now = new Date();

// –ü—Ä–æ–≤–µ—Ä—è–µ–º –∫–∞–∂–¥—ã–π –ø–æ—Ä–æ–≥
for (const [alertType, config] of Object.entries(thresholds)) {
  const value = parseFloat(reading[config.field]) || 0;

  // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∏—Ä—É–µ–º —Å–æ—Å—Ç–æ—è–Ω–∏–µ –µ—Å–ª–∏ –Ω–µ—Ç
  if (!states[alertType]) {
    states[alertType] = { acknowledged: false, mutedUntil: null };
  }

  const state = states[alertType];

  // –ü—Ä–æ–≤–µ—Ä—è–µ–º –∫—Ä–∏—Ç–∏—á–Ω–æ—Å—Ç—å
  let isCritical = false;
  if (config.max !== undefined && value > config.max) isCritical = true;
  if (config.min !== undefined && value < config.min) isCritical = true;

  // –ü—Ä–æ–≤–µ—Ä—è–µ–º muted
  const isMuted = state.mutedUntil && new Date(state.mutedUntil) > now;

  // –ï—Å–ª–∏ –∫—Ä–∏—Ç–∏—á–Ω–æ –∏ –Ω–µ acknowledged –∏ –Ω–µ muted - –æ—Ç–ø—Ä–∞–≤–ª—è–µ–º
  if (isCritical && !state.acknowledged && !isMuted) {
    alerts.push({
      alert_type: alertType,
      message: config.message,
      current_value: value,
      threshold_min: config.min,
      threshold_max: config.max,
      outdoor_temperature: reading.outdoor_temperature,
      outdoor_humidity: reading.outdoor_humidity,
      water_level: reading.water_level
    });
  }

  // –ï—Å–ª–∏ –Ω–µ –∫—Ä–∏—Ç–∏—á–Ω–æ - —Å–±—Ä–∞—Å—ã–≤–∞–µ–º acknowledged
  if (!isCritical && state.acknowledged) {
    state.acknowledged = false;
    state.mutedUntil = null;
  }
}

// –°–æ—Ö—Ä–∞–Ω—è–µ–º –æ–±–Ω–æ–≤–ª–µ–Ω–Ω—ã–µ —Å–æ—Å—Ç–æ—è–Ω–∏—è
staticData.alertStates = states;

if (alerts.length === 0) {
  return [];
}

return alerts.map(a => ({ json: a }));

const alert = $input.first().json;
const chatId = '–£–ö–ê–ñ–ò–¢–ï_–í–ê–®_TELEGRAM_CHAT_ID';

const currentValue = parseFloat(alert.current_value) || 0;

let message = `${alert.message}\n\n`;
message += `üìä *–¢–µ–∫—É—â–µ–µ –∑–Ω–∞—á–µ–Ω–∏–µ:* ${currentValue.toFixed(1)}\n`;

if (alert.threshold_min !== undefined) {
  message += `‚ö†Ô∏è *–ú–∏–Ω–∏–º—É–º:* ${alert.threshold_min}\n`;
}
if (alert.threshold_max !== undefined) {
  message += `‚ö†Ô∏è *–ú–∞–∫—Å–∏–º—É–º:* ${alert.threshold_max}\n`;
}

message += `\n‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n`;
message += `\nüìã *–í–°–ï –ü–û–ö–ê–ó–ê–¢–ï–õ–ò:*\n`;
message += `üå°Ô∏è –¢–µ–º–ø–µ—Ä–∞—Ç—É—Ä–∞: ${parseFloat(alert.outdoor_temperature).toFixed(1)}¬∞C\n`;
message += `üíß –í–ª–∞–∂–Ω–æ—Å—Ç—å: ${parseFloat(alert.outdoor_humidity).toFixed(1)}%\n`;
message += `üí¶ –£—Ä–æ–≤–µ–Ω—å –≤–æ–¥—ã: ${parseFloat(alert.water_level).toFixed(1)}%\n`;

return [{
  json: {
    chat_id: chatId,
    text: message,
    parse_mode: 'Markdown',
    reply_markup: {
      inline_keyboard: [[
        {
          text: '‚úÖ –ü–æ–¥—Ç–≤–µ—Ä–¥–∏—Ç—å',
          callback_data: `ack_${alert.alert_type}`
        },
        {
          text: 'üîï –û—Ç–∫–ª—é—á–∏—Ç—å –Ω–∞ 1—á',
          callback_data: `mute_${alert.alert_type}_1h`
        }
      ]]
    }
  }
}];
