const reading = $node["–ü–æ–ª—É—á–∏—Ç—å –ü–æ—Å–ª–µ–¥–Ω–∏–µ –î–∞–Ω–Ω—ã–µ"].json;
const states = $input.all().map(i => i.json);

// –ü—Ä–µ–æ–±—Ä–∞–∑—É–µ–º –≤ map –¥–ª—è –±—ã—Å—Ç—Ä–æ–≥–æ –¥–æ—Å—Ç—É–ø–∞
const statesMap = {};
states.forEach(s => {
  statesMap[s.alert_type] = s;
});

// –ü–æ—Ä–æ–≥–æ–≤—ã–µ –∑–Ω–∞—á–µ–Ω–∏—è
const thresholds = {
  high_temperature: { field: 'temperature', max: 35, message: 'üî• –ö–†–ò–¢–ò–ß–ù–û! –¢–µ–º–ø–µ—Ä–∞—Ç—É—Ä–∞ —Å–ª–∏—à–∫–æ–º –≤—ã—Å–æ–∫–∞—è' },
  low_temperature: { field: 'temperature', min: 18, message: '‚ùÑÔ∏è –ö–†–ò–¢–ò–ß–ù–û! –¢–µ–º–ø–µ—Ä–∞—Ç—É—Ä–∞ —Å–ª–∏—à–∫–æ–º –Ω–∏–∑–∫–∞—è' },
  high_humidity: { field: 'humidity', max: 85, message: 'üíß –ö–†–ò–¢–ò–ß–ù–û! –í–ª–∞–∂–Ω–æ—Å—Ç—å —Å–ª–∏—à–∫–æ–º –≤—ã—Å–æ–∫–∞—è' },
  low_humidity: { field: 'humidity', min: 20, message: 'üèúÔ∏è –ö–†–ò–¢–ò–ß–ù–û! –í–ª–∞–∂–Ω–æ—Å—Ç—å —Å–ª–∏—à–∫–æ–º –Ω–∏–∑–∫–∞—è' },
  low_water_level: { field: 'water_level', min: 5, message: 'üö® –ö–†–ò–¢–ò–ß–ù–û! –£—Ä–æ–≤–µ–Ω—å –≤–æ–¥—ã –æ—á–µ–Ω—å –Ω–∏–∑–∫–∏–π!' },
  high_wind_speed: { field: 'wind_speed', max: 11, message: 'üí® –ö–†–ò–¢–ò–ß–ù–û! –°–ª–∏—à–∫–æ–º —Å–∏–ª—å–Ω—ã–π –≤–µ—Ç–µ—Ä!' }
};

const alerts = [];

// –ü—Ä–æ–≤–µ—Ä–∫–∞ –Ω–∞ –ø–æ—Ç–µ—Ä—é —Å–≤—è–∑–∏ —Å ESP32
const lastUpdateTime = new Date(reading.received_at || reading.timestamp_iso).getTime();
const currentTime = new Date().getTime();
const minutesSinceUpdate = Math.floor((currentTime - lastUpdateTime) / 1000 / 60);

const connectionLostState = statesMap['esp32_connection_lost'];
const isConnectionLost = minutesSinceUpdate > 15;

// –ü—Ä–æ–≤–µ—Ä—è–µ–º mute_until –¥–ª—è –ø–æ—Ç–µ—Ä–∏ —Å–≤—è–∑–∏
const isConnectionLostMuted = connectionLostState && connectionLostState.mute_until && new Date(connectionLostState.mute_until) > new Date();
const isConnectionLostAcknowledged = connectionLostState && connectionLostState.acknowledged_at;

if (isConnectionLost && !isConnectionLostAcknowledged && !isConnectionLostMuted) {
  alerts.push({
    alert_type: 'esp32_connection_lost',
    message: 'üî¥ –ö–†–ò–¢–ò–ß–ù–û! –ü–æ—Ç–µ—Ä—è–Ω–∞ —Å–≤—è–∑—å —Å ESP32',
    field: 'connection',
    current_value: minutesSinceUpdate,
    threshold_max: 15,
    timestamp: reading.timestamp_iso,
    is_new: !connectionLostState || !connectionLostState.is_active
  });
} else if (!isConnectionLost && connectionLostState && (connectionLostState.is_active || connectionLostState.acknowledged_at)) {
  // –°–≤—è–∑—å –≤–æ—Å—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–∞ - –¥–µ–∞–∫—Ç–∏–≤–∏—Ä—É–µ–º –æ–ø–æ–≤–µ—â–µ–Ω–∏–µ
  alerts.push({
    alert_type: 'esp32_connection_lost',
    action: 'deactivate'
  });
}

// –ü—Ä–æ–≤–µ—Ä—è–µ–º –∫–∞–∂–¥–æ–µ —É—Å–ª–æ–≤–∏–µ
for (const [alertType, config] of Object.entries(thresholds)) {
  const value = parseFloat(reading[config.field]) || 0;
  const state = statesMap[alertType];

  let isCritical = false;

  // –ü—Ä–æ–≤–µ—Ä—è–µ–º –ø–æ—Ä–æ–≥–∏
  if (config.max !== undefined && value > config.max) {
    isCritical = true;
  }
  if (config.min !== undefined && value < config.min) {
    isCritical = true;
  }

  // –ü—Ä–æ–≤–µ—Ä—è–µ–º mute_until (–µ—Å–ª–∏ –æ—Ç–∫–ª—é—á–µ–Ω–æ –Ω–∞ –≤—Ä–µ–º—è)
  const isMuted = state && state.mute_until && new Date(state.mute_until) > new Date();

  // –ü—Ä–æ–≤–µ—Ä—è–µ–º –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏–µ - –µ—Å–ª–∏ –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–æ, –Ω–µ –æ—Ç–ø—Ä–∞–≤–ª—è–µ–º
  const isAcknowledged = state && state.acknowledged_at;

  // –ï—Å–ª–∏ –∫—Ä–∏—Ç–∏—á–Ω–æ –ò –ù–ï –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–æ –ò –ù–ï –æ—Ç–∫–ª—é—á–µ–Ω–æ - –æ—Ç–ø—Ä–∞–≤–ª—è–µ–º
  if (isCritical && !isAcknowledged && !isMuted) {
    alerts.push({
      alert_type: alertType,
      message: config.message,
      field: config.field,
      current_value: value,
      threshold_min: config.min,
      threshold_max: config.max,
      timestamp: reading.timestamp_iso,
      is_new: !state || !state.is_active
    });
  }

  // –ï—Å–ª–∏ –Ω–µ –∫—Ä–∏—Ç–∏—á–Ω–æ –Ω–æ –æ–ø–æ–≤–µ—â–µ–Ω–∏–µ –∞–∫—Ç–∏–≤–Ω–æ - –¥–µ–∞–∫—Ç–∏–≤–∏—Ä—É–µ–º –∏ —Å–±—Ä–∞—Å—ã–≤–∞–µ–º acknowledged
  if (!isCritical && state && (state.is_active || state.acknowledged_at)) {
    alerts.push({
      alert_type: alertType,
      action: 'deactivate'
    });
  }
}

if (alerts.length === 0) {
  // –ù–µ—Ç –æ–ø–æ–≤–µ—â–µ–Ω–∏–π
  return [];
}

return alerts.map(a => ({ json: a }));

const alert = $json;
const reading = $node["–ü–æ–ª—É—á–∏—Ç—å –ü–æ—Å–ª–µ–¥–Ω–∏–µ –î–∞–Ω–Ω—ã–µ"].json;

// –ë–µ–∑–æ–ø–∞—Å–Ω–æ–µ –ø–æ–ª—É—á–µ–Ω–∏–µ current_value —Å fallback
const currentValue = parseFloat(alert.current_value) || 0;

let message = '';

// –°–ø–µ—Ü–∏–∞–ª—å–Ω–æ–µ —Ñ–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ –¥–ª—è –ø–æ—Ç–µ—Ä–∏ —Å–≤—è–∑–∏
if (alert.alert_type === 'esp32_connection_lost') {
  const minutesSinceUpdate = Math.floor(currentValue);
  message = `${alert.message}\n\n`;
  message += `‚è±Ô∏è *–í—Ä–µ–º—è —Å –ø–æ—Å–ª–µ–¥–Ω–µ–≥–æ –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è:* ${minutesSinceUpdate} –º–∏–Ω\n`;
  message += `‚ö†Ô∏è *–ö—Ä–∏—Ç–∏—á–Ω–æ –ø–æ—Å–ª–µ:* ${alert.threshold_max} –º–∏–Ω\n`;
  message += `\nüïí *–ü–æ—Å–ª–µ–¥–Ω–µ–µ –æ–±–Ω–æ–≤–ª–µ–Ω–∏–µ:* ${new Date(alert.timestamp).toLocaleString('ru-RU', { timeZone: 'Europe/Moscow' })}\n`;
  message += `\n‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n`;
  message += `\nüìã *–ü–û–°–õ–ï–î–ù–ò–ï –ü–û–ö–ê–ó–ê–¢–ï–õ–ò:*\n`;
  message += `üå°Ô∏è –¢–µ–º–ø–µ—Ä–∞—Ç—É—Ä–∞: ${parseFloat(reading.temperature).toFixed(1)}¬∞C\n`;
  message += `üíß –í–ª–∞–∂–Ω–æ—Å—Ç—å: ${parseFloat(reading.humidity).toFixed(1)}%\n`;
  message += `üí¶ –£—Ä–æ–≤–µ–Ω—å –≤–æ–¥—ã: ${parseFloat(reading.water_level).toFixed(1)}%\n`;
  message += `üí® –í–µ—Ç–µ—Ä: ${parseFloat(reading.wind_speed).toFixed(1)} –º/—Å\n`;
  message += `\n‚ö†Ô∏è *–ü—Ä–æ–≤–µ—Ä—å—Ç–µ ESP32 –∏ –ø–æ–¥–∫–ª—é—á–µ–Ω–∏–µ –∫ –∏–Ω—Ç–µ—Ä–Ω–µ—Ç—É!*`;
} else {
  // –û–±—ã—á–Ω–æ–µ —Ñ–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ –¥–ª—è –¥—Ä—É–≥–∏—Ö –æ–ø–æ–≤–µ—â–µ–Ω–∏–π
  message = `${alert.message}\n\n`;
  message += `üìä *–¢–µ–∫—É—â–µ–µ –∑–Ω–∞—á–µ–Ω–∏–µ:* ${currentValue.toFixed(1)}\n`;

  if (alert.threshold_min !== undefined) {
    message += `‚ö†Ô∏è *–ú–∏–Ω–∏–º—É–º:* ${alert.threshold_min}\n`;
  }
  if (alert.threshold_max !== undefined) {
    message += `‚ö†Ô∏è *–ú–∞–∫—Å–∏–º—É–º:* ${alert.threshold_max}\n`;
  }

  message += `\nüïí *–í—Ä–µ–º—è:* ${new Date(alert.timestamp).toLocaleString('ru-RU', { timeZone: 'Europe/Moscow' })}\n`;
  message += `\n‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n`;
  message += `\nüìã *–í–°–ï –ü–û–ö–ê–ó–ê–¢–ï–õ–ò:*\n`;
  message += `üå°Ô∏è –¢–µ–º–ø–µ—Ä–∞—Ç—É—Ä–∞: ${parseFloat(reading.temperature).toFixed(1)}¬∞C\n`;
  message += `üíß –í–ª–∞–∂–Ω–æ—Å—Ç—å: ${parseFloat(reading.humidity).toFixed(1)}%\n`;
  message += `üí¶ –£—Ä–æ–≤–µ–Ω—å –≤–æ–¥—ã: ${parseFloat(reading.water_level).toFixed(1)}%\n`;
  message += `üí® –í–µ—Ç–µ—Ä: ${parseFloat(reading.wind_speed).toFixed(1)} –º/—Å\n`;
}

return [{
  json: {
    message: message,
    alert_type: alert.alert_type,
    current_value: currentValue,
    is_new: alert.is_new
  }
}];

// –ü–æ–¥–≥–æ—Ç–æ–≤–∫–∞ –¥–∞–Ω–Ω—ã—Ö –¥–ª—è –æ—Ç–ø—Ä–∞–≤–∫–∏ –≤ Telegram
const chatId = '–£–ö–ê–ñ–ò–¢–ï_–í–ê–®_TELEGRAM_CHAT_ID';
const text = $json.message;
const alertType = $json.alert_type;

// –§–æ—Ä–º–∏—Ä—É–µ–º —Ç–µ–ª–æ –∑–∞–ø—Ä–æ—Å–∞ –¥–ª—è Telegram API
const requestBody = {
  chat_id: chatId,
  text: text,
  parse_mode: 'Markdown',
  reply_markup: {
    inline_keyboard: [
      [
        {
          text: '‚úÖ –ü–æ–¥—Ç–≤–µ—Ä–¥–∏—Ç—å',
          callback_data: `ack_${alertType}`
        },
        {
          text: 'üîï –û—Ç–∫–ª—é—á–∏—Ç—å –Ω–∞ 1—á',
          callback_data: `mute_${alertType}_1h`
        }
      ]
    ]
  }
};

// –°–æ—Ö—Ä–∞–Ω—è–µ–º –¥–∞–Ω–Ω—ã–µ –¥–ª—è —Å–ª–µ–¥—É—é—â–∏—Ö —É–∑–ª–æ–≤
return [{ 
  json: {
    ...requestBody,
    alert_type: alertType,
    current_value: $json.current_value,
    message: text
  }
}];

// –ü–æ–¥–≥–æ—Ç–æ–≤–∫–∞ SQL –∑–∞–ø—Ä–æ—Å–∞ –¥–ª—è –¥–µ–∞–∫—Ç–∏–≤–∞—Ü–∏–∏
const alertType = $json.alert_type || 'unknown';

const sqlQuery = `UPDATE telegram_alert_states
SET 
  is_active = false,
  acknowledged_at = NULL,
  mute_until = NULL,
  updated_at = NOW()
WHERE alert_type = '${alertType}';`;

return [{ json: { sql_query: sqlQuery, alert_type: alertType } }];
